import java.util.*;

public class B211206 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		if (n == 1)
			System.out.println("-1");
		else
			System.out.println(n + " " + (n+1) + " " + n*(n+1));
		
	}

}
